import { CoreLayerOptions } from '../../core/core-layer';
import { OptionStyleAttribute, ShapeAttr } from '../../types';
/**
 * 点图层 图层样式
 */
export type PointLayerStyleOptions = {
    /**
     * 透明度
     */
    opacity?: OptionStyleAttribute<number>;
    /**
     * 描边宽度
     */
    strokeWidth?: number;
    /**
     * 描边颜色
     */
    stroke?: string;
};
/**
 * 点图层 图形形状
 */
export type PointShape2d = 'circle' | 'square' | 'hexagon' | 'triangle' | 'pentagon' | 'octogon' | 'hexagram' | 'rhombus' | 'vesica' | 'dot';
export type PointShape3d = 'cylinder' | 'triangleColumn' | 'hexagonColumn' | 'squareColumn';
export type PointShape = PointShape2d | PointShape3d | 'text';
/**
 * 点图层 文本相对锚点
 */
export type AnchorType = 'right' | 'top-right' | 'left' | 'bottom-right' | 'left' | 'top-left' | 'bottom-left' | 'bottom' | 'bottom-right' | 'bottom-left' | 'top' | 'top-right' | 'top-left' | 'center';
/**
 * 点图层 文本样式
 */
export type PointTextLayerStyleOptions = {
    opacity?: OptionStyleAttribute<number>;
    textAnchor?: AnchorType;
    textOffset?: [number, number];
    spacing?: number;
    padding?: [number, number];
    halo?: number;
    gamma?: number;
    stroke?: string;
    strokeWidth?: number;
    strokeOpacity?: number;
    fontFamily?: string;
    fontWeight?: string;
    textAllowOverlap?: boolean;
    iconfont?: true;
};
/**
 * 点图层配置
 */
export interface PointLayerOptions extends CoreLayerOptions {
    /**
     * 图形形状
     */
    shape?: ShapeAttr<PointShape | string>;
    /**
     * 图层样式
     */
    style?: PointLayerStyleOptions | PointTextLayerStyleOptions;
}
